import Container from "react-bootstrap/Container";
import Image from "react-bootstrap/Image";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { useTranslation } from "react-i18next";
import { useState } from "react";
import Form from "react-bootstrap/Form";
import i18next from "i18next";
import { Link } from "react-router-dom";

const PhotographersList = (props) => {
  const filterPhotographers = (f) => {
    return (
    photographers.map((p, i) => {
    if ((t(p.name)).match(f))
      return (
        <Row>
          <Col xs={8} className="mb-5 d-flex flex-column justify-content-start">
            <h2>{t(p.name)}</h2>
            <p className="lead ">{t(p.description)}</p>
            <Link to={"/photographer/" + (i + 1)}>
              <Button variant="secondary">{t("Страница фотографа")}</Button>
            </Link>
          </Col>
          <Col xs={4} className="mb-5 d-flex justify-content-end">
            <Image height="250" src={p.imageSrc} className="rounded"></Image>
          </Col>
          <hr></hr>
        </Row>
      );

    }));
  };
  const { t, i } = useTranslation();
  const { photographers } = props;
  const [search, setSearch] = useState("");
  i18next.on('languageChanged', () => {
    console.log("hello");
    setList(filterPhotographers(""));
  }); 
  const [list, setList] = useState(filterPhotographers(""));
  const handleClick = () => {
    setList(filterPhotographers(search));
  };

  return (
    <Container className="mt-5">
      <Form className="d-flex mb-5">
        <Form.Control
          type="search"
          placeholder={t("Поиск")}
          className="me-2"
          aria-label={t("Поиск")}
          onChange={(event) => setSearch(event.target.value)}
          label={search}
        ></Form.Control>
        <Button variant="secondary" onClick={handleClick}>
          {t("Поиск")}
        </Button >
      </Form>
      {list}
    </Container>
  );
};

export default PhotographersList;
