import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image'
import Button from 'react-bootstrap/Button';
import { useTranslation } from 'react-i18next';
import { Link } from 'react-router-dom';
import photographers from '../HardcodedPhotographers';

const Homepage = (props) => {
    const { t, i18n } = useTranslation();
    const {name, description, yearsOfLife, imageSrc} = props.photographer;
    console.log(imageSrc);
    return (
        <Container className='mt-5'>
            <Container className='d-flex justify-content-end position-absolute sm'>
                <Image  height="400" src={imageSrc} className='rounded' ></Image>    
            </Container>

            <h1 className='mb-5'>{t('Фотограф Дня')}</h1>
            <h2>{t(name)}</h2>

            <p className='mt-5'>{t('Годы жизни: ')}{t(yearsOfLife)}</p>
            <p className='lead text-break w-50'>{t(description)}</p>

            <Link to={'/photographer/' + (photographers.indexOf(props.photographer) + 1)} className='position-absolute'>
                <Button variant="secondary" padding >
                    {t('Страница фотографа')}
                </Button>
            </Link>

        </Container>
    );
}

export default Homepage;
